Item.method_code:
// First get the item that is opened in this window
var grid = this.grid;
var relationshipName = aras.getRelationshipTypeName(this.relationships.currTabID);
if (!grid || !relationshipName) {
    return alert("Could not find a relationship grid from this window");
}

var relatedItemIds = [];
var relatedItemType = "";
grid.getSelectedItemIDs().forEach(function(elem) {
    relatedItemIds.push(this.getRelatedItemProperty(elem, "id"));
    relatedItemType = this.getRelatedItemProperty(elem, "itemtype");
});

aras.addItemToPackageDef(relatedItemIds, aras.getItemTypeName(relatedItemType));
