Item.method_code:
// First get the item that is opened in this window
var grid = this.relationships.iframesCollection[this.relationships.currTabID].contentWindow.grid;
var relationshipName = aras.getRelationshipTypeName(this.relationships.currTabID);
if (!grid || !relationshipName) {
    return alert("Could not find a relationship grid from this window");
}

aras.addItemToPackageDef(grid.getSelectedItemIDs(), relationshipName);
